DROP FUNCTION IF EXISTS `recurveDownCategoryId`;
delimiter ;;
CREATE FUNCTION `recurveDownCategoryId`(categoryId VARCHAR(40))
 RETURNS varchar(10000) CHARSET utf8mb4
BEGIN
DECLARE sTemp VARCHAR(10000);
DECLARE sTempChd VARCHAR(4000);

SET sTemp= categoryId;
SET sTempChd = categoryId;

WHILE sTempChd IS NOT NULL DO
SELECT GROUP_CONCAT(a.id) INTO sTempChd FROM tbl_animal_category a WHERE FIND_IN_SET(a.pid, sTempChd)>0;

if sTempChd is not null then 
	SET sTemp= CONCAT(sTemp,',',sTempChd);
end if;

END WHILE;
RETURN sTemp;
END
;;
delimiter ;

